mapper.select:
SELECT MEMBER_ID, MEMBER_NAME
	FROM MEMBER
	WHERE MEMBER_NAME = #{MEMBER_NAME} 
	AND MEMBER_EMAIL = #{MEMBER_EMAIL} 
	AND MEMBER_DEL_GB = 'N'

SELECT  COUNT(*)
		FROM    MEMBER
		WHERE   MEMBER_ID = #{MEMBER_ID}
		AND     MEMBER_EMAIL = #{MEMBER_EMAIL}
		AND     MEMBER_DEL_GB = 'N'

mapper.update:
UPDATE MEMBER
SET MEMBER_DEL_GB = 'Y'
WHERE MEMBER_ID = #{MEMBER_ID}

UPDATE MEMBER
SET MEMBER_DEL_GB = 'Y'
WHERE MEMBER_ID = #{MEMBER_ID}
