mapper.select:
SELECT MEMBER_ID, MEMBER_NAME, MEMBER_EMAIL
	FROM MEMBER
	WHERE MEMBER_NAME = #{MEMBER_NAME}
	AND MEMBER_EMAIL = #{MEMBER_EMAIL} 
	AND MEMBER_DEL_GB = 'N'

SELECT	MEMBER_PW
		FROM    MEMBER
		WHERE   MEMBER_ID = #{MEMBER_ID}
		AND     MEMBER_EMAIL = #{MEMBER_EMAIL}
		AND     MEMBER_DEL_GB = 'N'

SELECT *
FROM MEMBER
WHERE MEMBER_PW=#{MEMBER_PW}
AND MEMBER_ID = #{MEMBER_ID}

mapper.update:
UPDATE MEMBER
SET MEMBER_DEL_GB = 'Y'
WHERE MEMBER_ID = #{MEMBER_ID}

UPDATE MEMBER
SET MEMBER_DEL_GB = 'N'
WHERE MEMBER_ID = #{MEMBER_ID}
